import React, { Component } from "react";
import TodoItems from "./TodoItems";
import "./TodoList.css";

class TodoList extends Component {
    constructor(props) {
        super(props);

        this.state = {
            items: [],
        };

        this.itemCount = 0;
        this.completedCount = 0;

        this.addItem = this.addItem.bind(this);
        this.deleteItem = this.deleteItem.bind(this);
        this.completeItem = this.completeItem.bind(this);
        this.increasePriorityItem = this.increasePriorityItem.bind(this);
        this.decreasePriorityItem = this.decreasePriorityItem.bind(this);
    }

    addItem(e) {
        if (this._inputElement.value !== "") {
            const newItem = {
                text: this._inputElement.value,
                key: Math.random(),
                priority: "medium",
                completed: false,
            };

            this.setState((prevState) => {
                return {
                    items: prevState.items.concat(newItem)
                };
            });
            this.itemCount++;
            this._inputElement.value = "";
        }

        console.log(this.state.items);

        e.preventDefault();
    }

    deleteItem(key) {
        const filteredItems = this.state.items.filter(function (item) {
            return (item.key !== key);
        });

        this.itemCount--;
        this.setState({
            items: filteredItems
        });
    }

    completeItem(key) {
        let totalCompleted = 0;
        const filteredItems = this.state.items.filter(function (item) {
            if (item.key === key) {
                item.completed = !item.completed;
            }
            if (item.completed === true) {
                totalCompleted++;
            }
            return (item);
        });

        this.completedCount = totalCompleted;
        this.setState({
            items: filteredItems
        });
    }

    increasePriorityItem(key) {
        const filteredItems = this.state.items.filter(function (item) {
            if (item.key === key) {
                if (item.priority === "high") {
                    console.log("high match");
                    return item;
                } else if (item.priority === "medium") {
                    console.log("medium match");
                    item.priority = "high";
                    return item;
                } else if (item.priority === "low") {
                    console.log("low match");
                    item.priority = "medium";
                    return item;
                }
            }
            return (item);
        });

        this.setState({
            items: filteredItems
        });
    }

    decreasePriorityItem(key) {
        const filteredItems = this.state.items.filter(function (item) {
            if (item.key === key) {
                if (item.priority === "high") {
                    console.log("high match");
                    item.priority = "medium";
                    return item;
                } else if (item.priority === "medium") {
                    console.log("medium match");
                    item.priority = "low";
                    return item;
                } else if (item.priority === "low") {
                    console.log("low match");
                    return item;
                }
            }
            return (item);
        });

        this.setState({
            items: filteredItems
        });
    }

    sortTasks() {
        const priorityOrder = ["high", "medium", "low"];
        const sortedItems = this.state.items.sort((a, b) => priorityOrder.indexOf(a.priority) - priorityOrder.indexOf(b.priority));

        this.setState({
            items: sortedItems
        });
    }

    render() {
        return (
            <div className="todoListMain">
                <div className="header">
                    <info>
                    <h2>{`Items Completed / Items Count`}</h2>
                    <h2>{` ${this.completedCount} / ${this.itemCount}`}</h2>
                    </info>
                    <form onSubmit={this.addItem}>
                        <input placeholder="enter task" ref={(a) => this._inputElement = a}>
                        </input>
                        <button type="submit">add</button>
                    </form>
                </div>
                <TodoItems entries={this.state.items} delete={this.deleteItem} complete={this.completeItem} increase={this.increasePriorityItem} decrease={this.decreasePriorityItem}/>
            </div>
        );
    }
}

export default TodoList;
