import React, { Component } from "react";
import { RiDeleteBinFill, RiArrowUpCircleFill, RiArrowDownCircleFill, RiCheckboxBlankLine, RiCheckboxFill } from 'react-icons/ri';


class TodoItems extends Component {
    constructor(props) {
        super(props);

        this.createTasks = this.createTasks.bind(this);
        this.complete = this.complete.bind(this);
    }

    delete(key) {
        this.props.delete(key);
    }

    complete(item) {
        this.props.complete(item);
    }

    createTasks(item) {
        return (
            <li>
                <section>
                    <div style={{alignItems: 'center'}}>
                        {item.completed === true ? <RiCheckboxFill size={30} onClick={() => this.complete(item)}/> : <RiCheckboxBlankLine size={30} onClick={() => this.complete(item)}/>}
                    </div>
                    <text>{item.text}</text>
                </section>
                <section>
                    <div>
                        <RiDeleteBinFill size={30} onClick={() => this.delete(item.key)}/>
                        <RiArrowUpCircleFill size={30} onClick={() => this.delete(item.key)}/>
                        <RiArrowDownCircleFill size={30} onClick={() => this.delete(item.key)}/>
                    </div>
                </section>
            </li>
        )
    }

    render() {
        const todoEntries = this.props.entries;
        const listItems = todoEntries.map(this.createTasks);

        return (
            <ol style={{justifyContent: 'space-between'}} className="theList">
                {listItems}
            </ol>
        );
    }
}

export default TodoItems;
