import React, { Component } from "react";
import { RiDeleteBinFill, RiArrowUpCircleFill, RiArrowDownCircleFill, RiCheckboxBlankLine, RiCheckboxFill } from 'react-icons/ri';

const divStyle = {
    flexDirection: 'row',  backgroundColor: '#987', justifyContent:  'space-between', alignItems: 'center'
}

class TodoItems extends Component {
    constructor(props) {
        super(props);

        this.createTasks = this.createTasks.bind(this);
    }

    delete(key) {
        this.props.delete(key);
    }

    createTasks(item) {
        return (
                <li>
                    <section>
                    <div style={{alignItems: 'center'}}>
                        <RiCheckboxBlankLine size={30} onClick={() => this.delete(item.key)} key={item.key}/>
                    </div>
                    <text>{item.text}</text>
                    </section>
                    <div>
                        <RiDeleteBinFill size={30} onClick={() => this.delete(item.key)} key={item.key}/>
                        <RiArrowUpCircleFill size={30} onClick={() => this.delete(item.key)} key={item.key}/>
                        <RiArrowDownCircleFill size={30} onClick={() => this.delete(item.key)} key={item.key}/>
                    </div>
                    </li>
        )
    }

    render() {
        const todoEntries = this.props.entries;
        const listItems = todoEntries.map(this.createTasks);

        return (
            <ol style={{justifyContent: 'space-between'}} className="theList">
                {listItems}
            </ol>
        );
    }
}

export default TodoItems;
